 SSE Curl # Get current-millis
Note that dev-id is used to track duplicate subscribers
curl --header &#34;request-id: 1650612377608#0#myDevId#myTaskName#public_api#my-inst-notes&#34; -N http://localhost:8080/v1/subscribe Quarkus Multiple Dev Instances # ./gradlew build cd signage/signage-server/signage-server-quarkus/build/quarkus-app # In separate terminals, do java -Dquarkus.http.port=8080 -jar quarkus-run.jar java -Dquarkus.http.port=8081 -jar quarkus-run.jar java -Dquarkus.http.port=8082 -jar quarkus-run.jar java -Dquarkus.http.port=8083 -jar quarkus-run.jar RestAssured Template # @Tag(TestMarker.ENDPOINT_TEST) class KeepaliveResourceTest { @Test void getCurrentServerVersion() { given() .baseUri(TestConfig.BASE_URI) // http://localhost:8080 .basePath(TestConfig.BASE_PATH) // /v1 .header(&#34;request-id&#34;, signage.entities.utils.tracer.Tracer.fake().getRequestId()) .when().get(&#34;/keepalive/getCurrentServerVersion&#34;) .then() .log().ifValidationFails() .statusCode(200); } } RestController Template # @PUT @Produces(MediaType.APPLICATION_JSON) @Consumes(MediaType.APPLICATION_JSON) @Path(&#34;/{playerId}/setNameNotes&#34;) public Response setPlayerNameNotes(@HeaderParam(&#34;request-id&#34;) String requestId, @PathParam(&#34;playerId&#34;) String playerId, NameNotes nameNotes) { String tracerValidity = TracerValidator.isRequestIdValid(requestId); if (!tracerValidity.equals(TracerValidator.VALID)) { return Response.status(Response.Status.BAD_REQUEST.getStatusCode(), Response.Status.BAD_REQUEST.getReasonPhrase() + &#34;: &#34; + tracerValidity).build(); } // TODO Add user id and tenant id Tracer tracer = new Tracer(requestId); tracer.setTenantId(&#34;tenant_brwsky&#34;); tracer.setUserId(&#34;aabuid&#34;); try { tracer.addTraceStart(TASK_SET_NAME_NOTES); signage.entities.common.NameNotes eNameNotes = new signage.entities.common.NameNotes(nameNotes.getName(), nameNotes.getNotes(), Language.getLanguageByCode(nameNotes.getLanguageCode().getValue())); SetPlayerNameNotesUsecaseFactoryProvider.getFactory().make().setPlayerNameNotes(tracer, playerId, eNameNotes); } catch (InvalidNameNotesException exception) { return ResponseBuilder.build(Response.Status.BAD_REQUEST, exception.getMessage()); } catch (UnauthorizedException exception) { return ResponseBuilder.build(Response.Status.UNAUTHORIZED, &#34;You don&#39;t have the necessary permissions to access a player details&#34;); } catch (NoSuchPlayerException exception) { return ResponseBuilder.build(Response.Status.NOT_FOUND, &#34;No such player with player-id: &#34; + playerId); } finally { tracer.addTraceEndWithMetric(TASK_SET_NAME_NOTES); } return Response.ok().build(); }
